mapper.select:
select
id, user_id, type, song_id, song_list_id, create_time
from collect


        and id = #{id}


        and user_id = #{userId}


        and type = #{type}


        and song_id = #{songId}


        and song_list_id = #{songListId}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from collect


        and id = #{id}


        and user_id = #{userId}


        and type = #{type}


        and song_id = #{songId}


        and song_list_id = #{songListId}


        and create_time = #{createTime}
